CREATE OR REPLACE VIEW Cene_obroka ( naz_st, opis_st, naz_ob, cena_ob, dat_zv, dat_pv ) AS
SELECT
    status_studenta.naz_st,
    status_studenta.opis_st,
    obrok.naz_ob,
    cenovnik.cena_ob,
    cenovnik.dat_zv,
    cenovnik.dat_pv
FROM
         cenovnik
    INNER JOIN obrok ON obrok.sifra_ob = cenovnik.ob_sifra_ob
    INNER JOIN status_studenta ON status_studenta.id_st = cenovnik.status_stud_id_st 
;

CREATE OR REPLACE VIEW Kupovina_hrane ( dat_izd_rac, isic_br, prez_stud, ime_stud, naz_st, iznos, kol_sta, stud_kartica_br_kar, naz_ob, kol_kar ) AS
SELECT
    racun.dat_izd_rac,
    studentska_kartica.isic_br,
    studentska_kartica.prez_stud,
    studentska_kartica.ime_stud,
    status_studenta.naz_st,
    stavka.iznos,
    stavka.kol_sta,
    racun.stud_kartica_br_kar,
    obrok.naz_ob,
    stanje_na_kartici.kol_kar
FROM
         studentska_kartica
    INNER JOIN racun ON studentska_kartica.br_kar = racun.stud_kartica_br_kar
    INNER JOIN status_studenta ON status_studenta.id_st = studentska_kartica.status_stud_id_st
    INNER JOIN stavka ON racun.id_rac = stavka.rac_id_rac
    INNER JOIN cenovnik ON cenovnik.dat_pv = stavka.cen_dat_pv
    INNER JOIN obrok ON obrok.sifra_ob = cenovnik.ob_sifra_ob
    INNER JOIN stanje_na_kartici ON obrok.sifra_ob = stanje_na_kartici.ob_sifra_ob AND stanje_na_kartici.stud_kartica_br_kar = studentska_kartica.br_kar 
;

