CREATE OR REPLACE VIEW Kolicina_po_rad ( sifra_rad, prez_rad, ime_rad, kolicina_dor) AS
SELECT 
    radnik_u_menzi.sifra_rad,radnik.prez_rad,radnik.ime_rad,sum(dnevno_posluzenje_hrane.kol_ph)
FROM 
    dnevno_posluzenje_hrane
    INNER JOIN obrok ON obrok.sifra_ob = dnevno_posluzenje_hrane.ob_sifra_ob
    INNER JOIN radnik_u_menzi ON radnik_u_menzi.sifra_rad = dnevno_posluzenje_hrane.rad_u_menzi_sifra_rad
    INNER JOIN radnik ON radnik.sifra_rad = radnik_u_menzi.sifra_rad
group by radnik_u_menzi.sifra_rad,radnik.prez_rad,radnik.ime_rad
;

CREATE OR REPLACE VIEW Osnovni_podaci ( opis_st, naz_st, naz_smera, kol_kar, naz_ob, br_kar, isic_br, jmbg_stud, br_ind, god_upisa, prez_stud, dat_rodj_stud, dat_vaz_kar, ime_stud, dat_vadj_kar, dat_vaz_st, ulica, br_ulice, naz_fak, grad_mr, drzava_mr, grad_ms, drzava_ms ) AS
SELECT
    status_studenta.opis_st,
    status_studenta.naz_st,
    smer.naz_smera,
    stanje_na_kartici.kol_kar,
    obrok.naz_ob,
    studentska_kartica.br_kar,
    studentska_kartica.isic_br,
    studentska_kartica.jmbg_stud,
    studentska_kartica.br_ind,
    studentska_kartica.god_upisa,
    studentska_kartica.prez_stud,
    studentska_kartica.dat_rodj_stud,
    studentska_kartica.dat_vaz_kar,
    studentska_kartica.ime_stud,
    studentska_kartica.dat_vadj_kar,
    studentska_kartica.dat_vaz_st,
    studentska_kartica.ulica,
    studentska_kartica.br_ulice,
    fakultet.naz_fak,
    grad_mr.naz_grada       AS "grad_mr",
    drzava_mr.naz_drzave    AS "drzava_mr",
    grad_ms.naz_grada       AS "grad_ms",
    drzava_ms.naz_drzave    AS "drzava_ms"
FROM
         studentska_kartica
    INNER JOIN stanje_na_kartici ON studentska_kartica.br_kar = stanje_na_kartici.stud_kartica_br_kar
    INNER JOIN obrok ON obrok.sifra_ob = stanje_na_kartici.ob_sifra_ob
    INNER JOIN grad    grad_mr ON grad_mr.id_grada = studentska_kartica.id_grada_mr
    INNER JOIN drzava  drzava_mr ON drzava_mr.id_drzave = grad_mr.drzava_id_drzave
    INNER JOIN grad    grad_ms ON grad_ms.id_grada = studentska_kartica.id_grada_ms
    INNER JOIN drzava  drzava_ms ON drzava_ms.id_drzave = grad_ms.drzava_id_drzave
    INNER JOIN smer ON smer.sifra_smera = studentska_kartica.smer_sifra_smera
    INNER JOIN fakultet ON fakultet.id_fak = smer.fak_id_fak
    INNER JOIN status_studenta ON status_studenta.id_st = studentska_kartica.status_stud_id_st 
;

CREATE OR REPLACE VIEW Prodaja_obroka ( ime_rad, prez_rad, dat_izd_rac, uk_cena, kol_sta, iznos, cena_ob, naz_st, naz_ob, id_kase, sifra_rad ) AS
SELECT
    radnik.ime_rad,
    radnik.prez_rad,
    racun.dat_izd_rac,
    racun.uk_cena,
    stavka.kol_sta,
    stavka.iznos,
    cenovnik.cena_ob,
    status_studenta.naz_st,
    obrok.naz_ob,
    kasa.id_kase,
    radnik_u_szuo.sifra_rad
FROM
         racun
    INNER JOIN kasa ON kasa.id_kase = racun.kasa_id_kase
    INNER JOIN radnik_u_szuo ON radnik_u_szuo.sifra_rad = racun.radnik_u_szuo_sifra_rad
    INNER JOIN radnik ON radnik.sifra_rad = radnik_u_szuo.sifra_rad
    INNER JOIN stavka ON racun.id_rac = stavka.rac_id_rac
    INNER JOIN cenovnik ON cenovnik.dat_pv = stavka.cen_dat_pv
    INNER JOIN status_studenta ON status_studenta.id_st = cenovnik.status_stud_id_st
    INNER JOIN obrok ON obrok.sifra_ob = cenovnik.ob_sifra_ob
    INNER JOIN radnik_u_szuo ON radnik.sifra_rad = radnik_u_szuo.sifra_rad 
;

